    flexDirection: "row",  
    alignItems: "center",  
    justifyContent: "space-between",  
    paddingHorizontal: 16,  
    paddingVertical: 12,  
    backgroundColor: "#fff",  
    borderBottomWidth: 1,  
    borderBottomColor: "#e5e5e5",  
  },  
  backButton: {  
    padding: 8,  
  },  
  headerTitle: {  
    fontSize: 18,  
    fontWeight: "600",  
    color: "#111",  
    flex: 1,  
    textAlign: "center",  
  },  
  headerActions: {  
    flexDirection: "row",  
    gap: 8,  
  },  
  headerActionBtn: {  
    padding: 8,  
  },  
    
  scrollView: {  
    flex: 1,  
  },  
  communityInfo: {  
    backgroundColor: "#fff",  
    padding: 20,  
    alignItems: "center",  
    borderBottomWidth: 1,  
    borderBottomColor: "#e5e5e5",  
  },  
  communityImage: {  
    width: 80,  
