  },  
  joinButtonText: {  
    color: "#fff",  
    fontWeight: "600",  
    fontSize: 14,  
  },  
  joinedButtonText: {  
    color: "#666",  
  },  
  inviteButton: {  
    backgroundColor: "#f0f7ff",  
    paddingVertical: 10,  
    paddingHorizontal: 24,  
    borderRadius: 20,  
    borderWidth: 1,  
    borderColor: "#2673f3",  
  },  
  inviteButtonText: {  
    color: "#2673f3",  
    fontWeight: "600",  
    fontSize: 14,  
  },  
    
  // Tabs CORREGIDOS - Desplazables horizontalmente  
  tabsScrollView: {  
    backgroundColor: "#fff",  
    borderBottomWidth: 1,  
    borderBottomColor: "#e5e5e5",  
  },  
  tabsContainer: {  
