    fontWeight: "600",  
  },  
    
  // Posts Content CORREGIDO  
  postsContent: {  
    backgroundColor: "#fff",  
    padding: 16,  
  },  
    
  // Post Creation COMPACTO  
  postCreationCompact: {  
    flexDirection: "row",  
    alignItems: "center",  
    backgroundColor: "#f8f9fa",  
    borderRadius: 25,  
    paddingHorizontal: 16,  
    paddingVertical: 12,  
    marginBottom: 16,  
    gap: 12,  
  },  
  userAvatarSmall: {  
    width: 32,  
    height: 32,  
    borderRadius: 16,  
  },  
