import { useState, useEffect, useCallback } from "react"  
import { View, Text, TouchableOpacity, StyleSheet, SafeAreaView, ScrollView, TextInput, RefreshControl } from "react-native"  
import { useTranslation } from "react-i18next"  
import { Search, TrendingUp, TrendingDown, Home, Users, MessageCircle, Bell, User } from "lucide-react-native"  
import { LanguageToggle } from "../components/LanguageToggle"  
import { useAuthGuard } from "../hooks/useAuthGuard"  
import { getMarketData, getFeaturedStocks } from "../rest/api"  
import { useSafeAreaInsets } from 'react-native-safe-area-context'  

interface Stock {  
  id: string  
  symbol: string  
  company_name: string  
  current_price: number  
  price_change: number  
  price_change_percent: number  
  color: string  
  is_featured: boolean  
}  

export function MarketInfoScreen({ navigation }: any) {  
  const { t } = useTranslation()  
  const insets = useSafeAreaInsets()  
  const [searchQuery, setSearchQuery] = useState("")  
  const [stocks, setStocks] = useState<Stock[]>([])  
  const [featuredStocks, setFeaturedStocks] = useState<Stock[]>([])  
  const [loading, setLoading] = useState(true)  
  const [refreshing, setRefreshing] = useState(false)  

  useAuthGuard()  

  const loadMarketData = useCallback(async () => {  
    try {  
      setLoading(true)  
      const [allStocks, featured] = await Promise.all([  
        getMarketData(),  
        getFeaturedStocks()  
      ])  
      setStocks(allStocks)  
      setFeaturedStocks(featured)  
    } catch (error) {  
      console.error('Error loading market data:', error)  
    } finally {  setLoading(false)  
      setRefreshing(false)  
    }  
  }, [])  

  useEffect(() => {  
    loadMarketData()  
  }, [loadMarketData])  

  const onRefresh = useCallback(() => {  
    setRefreshing(true)  
    loadMarketData()  
  }, [loadMarketData])  

  const filteredStocks = stocks.filter(stock =>   
    stock.symbol.toLowerCase().includes(searchQuery.toLowerCase()) ||  
    stock.company_name.toLowerCase().includes(searchQuery.toLowerCase())  
  )  

  return (  
    <View style={[styles.container, { paddingTop: insets.top }]}>  
      {/* Header con título y búsqueda */}  
      <View style={styles.header}>  
        <View style={styles.headerContent}>  
          <Text style={styles.headerTitle}>Información de la Bolsa</Text>  
          <LanguageToggle />  
        </View>  

        <View style={styles.searchContainer}>  
          <Search size={20} color="#667" />  
          <TextInput  
            style={styles.searchInput}  
            placeholder="Buscar acciones..."  
            placeholderTextColor="#999"  
            value={searchQuery}  
            onChangeText={setSearchQuery}  
          />  
        </View>  
      </View>  

      <ScrollView   
        style={styles.scrollView}   
        showsVerticalScrollIndicator={false}  
        refreshControl={  
          <RefreshControl  
            refreshing={refreshing}  
            onRefresh={onRefresh}  
            colors={['#2673f3']}  
            tintColor="#2673f3"  
          />  
        }  
      >  
        {/* Sección de acciones destacadas */}  
        <View style={styles.section}>  
          <View style={styles.sectionHeader}>  
            <Text style={styles.sectionTitle}>Actualizaciones Relevantes</Text>  
            <TouchableOpacity>  
              <Text style={styles.seeAllText}>Ver todo</Text>  
            </TouchableOpacity>  
          </View>  

          <View style={styles.featuredStocks}>  
            {featuredStocks.slice(0, 2).map((stock, index) => (  
              <View key={stock.id} style={[styles.featuredStock, { backgroundColor: stock.color }]}>  
                <Text style={styles.featuredSymbol}>{stock.symbol}</Text>  
                <Text style={styles.featuredCompany}>{stock.company_name}</Text>  
                <Text style={styles.featuredPrice}>${stock.current_price}</Text>  
                <Text style={styles.featuredChange}>  
                  {stock.price_change_percent > 0 ? '+' : ''}{stock.price_change_percent}%  
                </Text>  
              </View>  
            ))}  
          </View>  
        </View>  

        {/* Sección del mercado actual */}  
        <View style={styles.section}>  
          <View style={styles.sectionHeader}>  
            <Text style={styles.sectionTitle}>Mercado Actual</Text>  
            <TouchableOpacity>  
              <TrendingUp size={20} color="#667" />  
            </TouchableOpacity>  
          </View>  

          <View style={styles.stocksList}>  
            {filteredStocks.map((stock) => (  
              <TouchableOpacity key={stock.id} style={styles.stockItem}>  
                <View style={[styles.stockIcon, { backgroundColor: stock.color }]}>  
                  <Text style={styles.stockIconText}>{stock.symbol.charAt(0)}</Text>  
                </View>  

                <View style={styles.stockInfo}>  
                  <Text style={styles.stockSymbol}>{stock.symbol}</Text>  
                  <Text style={styles.stockCompany}>{stock.company_name}</Text>  
                </View>  

                <View style={styles.stockPrice}>  
                  <Text style={styles.stockPriceText}>${stock.current_price}</Text>  
                  <View style={styles.stockChange}>  
                    {stock.price_change_percent > 0 ? (  
                      <TrendingUp size={16} color="#10B981" />  
                    ) : (  
                      <TrendingDown size={16} color="#EF4444" />  
                    )}  
                    <Text style={[  
                      styles.stockChangeText,   
                      { color: stock.price_change_percent > 0 ? "#10B981" : "#EF4444" }  
                    ]}>  
                      {stock.price_change_percent > 0 ? '+' : ''}{stock.price_change_percent}%  
                    </Text>  
                  </View>  
                </View>  
              </TouchableOpacity>  
            ))}  
          </View>  

          {filteredStocks.length === 0 && searchQuery.trim() && (  
            <View style={styles.emptyState}>  
              <Text style={styles.emptyStateText}>No se encontraron resultados</Text>  
              <Text style={styles.emptyStateSubtext}>Intenta con otros términos de búsqueda</Text>  
            </View>  
          )}  
        </View>  
      </ScrollView>  

      {/* Navbar igual al HomeFeed */}  
      <View style={[styles.navbar, { paddingBottom: insets.bottom }]}>  
        <TouchableOpacity   
          style={styles.navItem}  
          onPress={() => navigation.navigate('HomeFeed')}  
        >  
          <Home size={24} color="#666" />  
          <Text style={styles.navText}>Inicio</Text>  
        </TouchableOpacity>  

        <TouchableOpacity   
          style={styles.navItem}  
          onPress={() => navigation.navigate('Communities')}  
        >  
          <Users size={24} color="#666" />  
          <Text style={styles.navText}>Comunidades</Text>  
        </TouchableOpacity>  

        <TouchableOpacity   
          style={styles.navItem}  
          onPress={() => navigation.navigate('Messages')}  
        >  
          <MessageCircle size={24} color="#666" />  
          <Text style={styles.navText}>Mensajes</Text>  
        </TouchableOpacity>  

        <TouchableOpacity   
          style={styles.navItem}  
          onPress={() => navigation.navigate('Notifications')}  
        >  
          <Bell size={24} color="#666" />  
          <Text style={styles.navText}>Notificaciones</Text>  
        </TouchableOpacity>  

        <TouchableOpacity   
          style={[styles.navItem, styles.activeNavItem]}  
        >  
          <TrendingUp size={24} color="#2673f3" />  
          <Text style={[styles.navText, styles.activeNavText]}>Mercado</Text>  
        </TouchableOpacity>  
      </View>  
    </View>  
  )  
}  

